import { useState } from 'react';
import { Header } from '../../components/Header';
import Imgbackground from '../../assets/github-white.svg';
import "./styles.css";
import ItemList from '../../components/itemList';

function App() {
  const [user, setUser] = useState('');
  const [currentUser, setCurrentUser] = useState(null);
  const [repos, setRepos] = useState(null);

  const handleGetData = async () => {
    const userData = await fetch(`https://api.github.com/users/${user}`);
    const newUser = await userData.json();

    if(newUser.name) {
      const {avatar_url, name, bio} = newUser;
      setCurrentUser({avatar_url, name, bio});
    }

    const reposData = await fetch(`https://api.github.com/users/${user}/repos`);
    const newRepos = await userData.json();

    if (newRepos.length) {
      setRepos(newRepos);
    }
  }



  return (
    <div className="App">
      <Header />
      <div className="content">
        <img src={Imgbackground} className="backgroundImg" alt="background img app "/>
        <div className="info">  
        <div> 
          <input name="user" value={user} onChange={event => setUser(event.target.value)} placeholder="@username" />
          <button onClick={handleGetData}>Buscar</button>
        </div>
        {currentUser.name}
        <div className='profile'>
          <img src="https://avatars.githubusercontent.com/u/87381791?s=400&u=003aa6ebaedb545cf2fe1276b3613ff9fd3b2424&v=4" className='profileImg' alt='Profile' />
          <div>
            <h3>Guilherme Fernandez</h3>
            <span>@guilhermefn</span>
            <p>Descrição</p>
          </div>
        </div>
        <hr />
        <div>
          <h4 className='repository'>Repositórios</h4>
          <ItemList title="teste1" description="teste de descrição" />
          <ItemList title="teste1" description="teste de descrição" />
          <ItemList title="teste1" description="teste de descrição" />
        </div>
        </div>
      </div>
    </div>
  );
}

export default App;
